import React from 'react';
import './App.css';
import Summary from './Summary.js';
import Grid from '@material-ui/core/Grid';
import Cookies from 'universal-cookie';
const cookies = new Cookies();



class Profile extends React.Component {
  constructor(){
    super();
    this.state = {
      start: 0,
      end: 2,
      summary: ""
    }
    this.getSummaries = this.getSummaries.bind(this);
  }

  getSummaries(){
    var userID = cookies.get('userID');
    userID = '0001';
    var api_url = `http://127.0.0.1:5000/get_summaries?user=${userID}&start=${this.state.start}&end=${this.state.end}`
    fetch(api_url)
        .then((res) => res.json())
        .then((resJson) => {
          this.setState({
            summary: resJson["summaries"][0]["summary"]
          })
        }).catch((error)=>console.log(error));
    
    return(
          <div>
            <h1>
              Profile Page
            </h1>
            <p>
              {this.state.summaries[0]}
            </p>
          </div>
        )

