import React from 'react';
import './Home.css';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import books from '../Images/books.jpg';
import {Link} from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Summary from '../Summary.js'
import data from '../data.json'
const useStyles = makeStyles((theme) => ({
}));

function Home() {
  const classes = useStyles();
  const info = data.users.user0001.cards
  return (
    <React.Fragment>
      <div className="middleSection"
        style={{
          backgroundImage:`url(${books})`
      }}>
        <Box component="span" m={1} className="header" style={{textAlign:"left", color:"white"}}>
          <h1 style={{paddingLeft: 50, textDecorationLine: 'underline', fontSize: 50}}>Welcome to Breakdown</h1>
          <p style={{paddingLeft: 50, fontSize: 20}}>Trim down the minor details to produce quick summaries of scholarly articles with Breakdown.</p>
        </Box>
        <Box component="span" m={1} className="header" style={{textAlign:"center", color:"white"}}>
          <p style={{fontSize: 30, paddingTop: 55}}>{info[0].date}</p>
          <Button
            variant="contained"
            color="default"
            className={classes.button}
            startIcon={<CloudUploadIcon />}
            component={Link} to="/upload"
          >
            Upload
        </Button>
        </Box>
      </div>
      <div style={{paddingLeft:'10%'}}>
        <Grid container spacing={4}>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Game Theory' title='Risk-Averse Equilibrium Analysis and Computation' summary='Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore 
            et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.' url='https://arxiv.org/pdf/2004.02470.pdf'/>
          </Grid>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Sports' title='ttrtr' summary='fdfdsfds' url='google.com'/>
          </Grid>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Sports' title='ttrtr' summary='fdfdsfds' url='google.com'/>
          </Grid>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Sports' title='ttrtr' summary='fdfdsfds' url='google.com'/>
          </Grid>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Sports' title='ttrtr' summary='fdfdsfds' url='google.com'/>  
          </Grid>
          <Grid item xs={6} sm={6}>
            <Summary date='4/14/20' time='12:34PM EST' genre='Sports' title='ttrtr' summary='fdfdsfds' url='google.com'/>
          </Grid>
        </Grid>
      </div>
    </React.Fragment>
  );
}

export default Home;
