import React from 'react';
import './App.css';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import TextField from '@material-ui/core/TextField';
import books from './Images/books.jpg'

class Upload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loadingSummary: false,
      url: "",
      title: "",
      success: 0
    }
    this.handleChange = this.handleChange.bind(this);
  }

  uploadSummary(){
    
  }

  handleUploadPress(){
    this.setState({ loadingSummary: true})
  }

  handleChange(event) {
    this.setState({[event.target.name]: event.target.value}, () => this.handleUploadPress());
  }

  preUpload(){
    return <div className="middleSection"
    style={{
      backgroundImage:`url(${books})`
  }}>
    <Box component="span" m={1} className="header" style={{textAlign:"center", color:"white"}}>
      <p style={{fontSize: 30}}>Enter link and author here</p>
    </Box>
    <Box component="span" m={1} className="header" style={{textAlign:"center", color:"white"}}>
      <TextField name="url" label="Insert Link Here" variant='filled' onChange={this.handleChange} style={{backgroundColor: 'white'}}/> <br/><br/>
      <TextField name="title" label="Insert Author Here" variant='filled' onChange={this.handleChange} style={{backgroundColor: 'white'}}/> <br/><br/>
      <Button
        variant="contained"
        color="default"
        startIcon={<CloudUploadIcon />}
      >
        Upload
    </Button>
    </Box>
  </div>
  }

  render() {
    if(this.state.loadingSummary){
      return (
        <React.Fragment>
          <p style={{fontSize: 30}}>False</p>
        </React.Fragment>
      );
    }else {
      return (
        <React.Fragment>
          {this.preUpload()}
        </React.Fragment>
      );
    }
  }
}

export default Upload;
